SELECT COALESCE(index_count,0) index_count,
       _membercard_dim 
  FROM (
        SELECT COALESCE(count(DISTINCT mallcoo_id),0) index_count,
               IF(_membercard IS NULL OR trim(cast(_membercard AS STRING))='', '未知',cast(_membercard AS STRING)) _membercard_dim 
          FROM (
                SELECT *,
                       _membercard_id IS NOT NULL AS _is_member 
                  FROM medusa.kudu_user
