--以店铺为研究对象
--按单位固定租金查看租金的总和

SELECT COALESCE(index_count,0) index_count,
       custommeasure413_dim 
  FROM (
        SELECT COALESCE(sum(_rent),0) index_count,
               IF(customMeasure413 IS NULL OR trim(cast(customMeasure413 AS STRING))='', '未知',cast(customMeasure413 AS STRING)) custommeasure413_dim 
          FROM (
                SELECT *,cast(_rent/_shop_area*12/365 as decimal(38,2)) customMeasure413 
                  FROM medusa_shop.kudu_current_user
                ) _t 
          WHERE id BETWEEN '1-' AND '1-z' 
            AND distinct_id=mallcoo_id 
          GROUP BY custommeasure413_dim
        ) x;
                 
