CREATE VIEW medusa_shop.kudu_current_user AS                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             |
SELECT concat(projectid, '-', mallcoo_id) id, 
       id current_id, 
	   mallcoo_id, 
	   distinct_id, 
	   `_shop_id`, 
	   `_shop_name`, 
	   `_shop_code`, 
	   `_mall_id`, 
	   `_mall_name`, 
	   `_door_no`, 
	   `_floor_id`, 
	   `_floor_name`, 
	   `_x`, 
	   `_y`, 
	   `_shop_area`, 
	   `_brand`, 
	   `_commercial_type`, 
	   `_sub_commercial_type`, 
	   `_rent`, 
	   `_rent_type`, 
	   `_sales_point`, 
	   `_settled_time`, 
	   `_update_time`, 
	   idlist, 
	   projectid, 
	   `_tag_temp`, 
	   `_tag`, 
	   `_status` 
  FROM (
        SELECT *, 
		       row_number() OVER (PARTITION BY mallcoo_id, projectid ORDER BY `_update_time` DESC) r 
		  FROM medusa_shop.kudu_user 
		 WHERE mallcoo_id = distinct_id
		) x 
  WHERE r = 1



